[cc lang=&ldquo;properties&rdquo;]{ /* Context Menu */ &ldquo;\U0x3fc&rdquo; = &ldquo;rightMouseDown:&quot;; /* Fun-C */ &ldquo;^;&rdquo; = &ldquo;rightMouseDown:&quot;; /* Ctrl-; */
/* Vim movement while Ctrl key down */ &ldquo;^h&rdquo; = (&ldquo;moveLeft:&quot;); &ldquo;^j&rdquo; = (&ldquo;moveDown:&quot;); &ldquo;^k&rdquo; = (&ldquo;moveUp:&quot;); &ldquo;^l&rdquo; = (&ldquo;moveRight:&quot;);
